parameters.functionScript:
/**
 * @role Détecte le type d'appareil utilisé (desktop, tablet ou mobile) et le renvoie en sortie.
 * @output {string} Outputs.DeviceType - "desktop", "tablet" ou "mobile".
 */
try {
    // Méthode principale : user agent
    let deviceType = "desktop";
    const ua = navigator.userAgent || navigator.vendor || window.opera;

    if (/android/i.test(ua)) {
        deviceType = /mobile/i.test(ua) ? "mobile" : "tablet";
    } else if (/iPad|Tablet|PlayBook|Silk/i.test(ua)) {
        deviceType = "tablet";
    } else if (/iPhone|iPod|Mobile/i.test(ua)) {
        deviceType = "mobile";
    } else {
        // Méthode de secours : largeur d'écran
        const w = window.innerWidth;
        if (w <= 800) {
            deviceType = w <= 600 ? "mobile" : "tablet";
        }
    }

    Outputs.DeviceType = deviceType;
    Outputs.Success();
} catch (err) {
    Outputs.Failure("Erreur lors de la détection du type d'appareil : " + err.message);
}

